sourcesContent:
import apartment from '../models/apartment.js';
import payment from '../models/payment.js'


const createApartment = async (req, res) => {
  const { managerId, owner, floor, price, paid, cin } = req.body;


  try {
    const newApartment = new apartment({
      Manager_id: managerId,
      owner: owner,
      etage: floor,
      cin: cin,
      price: price || 0,
      paid: paid || false,
    });


    const savedApartment = await newApartment.save();

    const newPayment = new payment({

      userId: managerId,
      apartment: newApartment._id,
      Pay_Date: Date.now(),

    });

    const savedPayment = await newPayment.save();

    const result = {
      savedApartment: savedApartment,
      savedPayment: savedPayment
    };

    res.status(201).json(result);
  } catch (error) {
    console.error('Error creating apartment:', error);
    res.status(500).json({ error: 'Could not create apartment' });
  }
};



const getAll = async (req, res) => {
  try {
    const apartments = await apartment.find();
    res.status(200).json(apartments);
  } catch (error) {
    res.status(404).json({ error: 'Could not fetch apartments' });
  }
};



const updateApartment = async (req, res) => {
  const { id } = req.params;
  const { owner, floor, price, paid } = req.body;

  try {
    const updatedApartment = await apartment.findByIdAndUpdate(
      id,
      {
        owner: owner,
        etage: floor,
        price: price || 0,
        paid: paid || false,
      },
      { new: true }
    );

    if (!updatedApartment) {
      return res.status(404).json({ error: 'Apartment not found' });
    }

    res.status(200).json(updatedApartment);
  } catch (error) {
    res.status(500).json({ error: 'Could not update apartment' });
  }
};



const searchByOwnerName = async (req, res) => {
  const { ownerName } = req.query;

  try {
    const apartments = await apartment.find({ owner: { $regex: new RegExp(ownerName, 'i') } });

    if (apartments.length === 0) {
      return res.status(404).json({ error: 'No apartments found for this owner name' });
    }

    res.status(200).json(apartments);
  } catch (error) {
    console.error('Error searching apartments by owner name:', error);
    res.status(500).json({ error: 'Could not perform owner name search' });
  }
};



const getPaidApartments = async (req, res) => {
  try {
    const paidApartments = await apartment.find({ paid: true });

    if (paidApartments.length === 0) {
      return res.status(404).json({ message: 'No paid apartments found' });
    }

    res.status(200).json(paidApartments);
  } catch (error) {
    console.error('Error retrieving paid apartments:', error);
    res.status(500).json({ error: 'Could not retrieve paid apartments' });
  }
};


const markApartmentAsPaid = async (req, res) => {
  const apartmentId = req.params.apartmentId;
  try {
    const foundApartment = await apartment.findById(apartmentId);

    if (!foundApartment) {
      return res.status(404).json({ error: 'Apartment not found' });
    }

    foundApartment.paid = true;

    const updatedApartment = await foundApartment.save();


    const foundPayment = await payment.findOne({ apartment: apartmentId });


    if (!foundPayment) {
      throw new Error('Payment not found for the apartment');
    }


    const monthNumber = new Date().getMonth() + 1;

    const monthMap = {
      1: 'January',
      2: 'February',
      3: 'March',
      4: 'April',
      5: 'May',
      6: 'June',
      7: 'July',
      8: 'August',
      9: 'September',
      10: 'October',
      11: 'November',
      12: 'December',
    };

    const monthName = monthMap[monthNumber];

    foundPayment.monthsPaid.push(monthName); 

    const updatedPayment = await foundPayment.save();

    const result = {
      updatedApartment: updatedApartment,
      updatedPayment: updatedPayment
    };

    res.status(201).json(result);

  } catch (error) {
    console.error('Error toggling apartment payment status:', error);
    res.status(500).json({ error: 'Could not update apartment payment status' });
  }
};



const deleteApartment = async (req, res) => {
  const apartmentId = req.params.apartmentId;

  try {

    const foundApartment = await apartment.findById(apartmentId);

    if (!foundApartment) {
      return res.status(404).json({ error: 'Apartment not found' });
    }
    const deletedApartment = await foundApartment.deleteOne();

    res.status(200).json({ message: 'Apartment deleted successfully', deletedApartment });
  } catch (error) {
    res.status(500).json({ error: 'Could not delete apartment' });
  }
};





const filterApartmentsByMonth = async (req, res) => {
  try {
    const  {month}  = req.params; 

    const foundApartments = await payment.find({
      monthsPaid: { $elemMatch: { $eq: month } }
    }).populate('apartment');

    res.status(200).json(foundApartments);
  } catch (error) {
    console.error('Error filtering apartments by month:', error);
    res.status(500).json({ error: 'Could not filter apartments by month' });
  }
};








export { createApartment, getAll,filterApartmentsByMonth, markApartmentAsPaid,updateApartment, searchByOwnerName, getPaidApartments, deleteApartment }

