sourcesContent:
import { Router } from "express";
import {createApartment , getAll,filterApartmentsByMonth ,updateApartment,markApartmentAsPaid ,searchByOwnerName, getPaidApartments ,deleteApartment} from "../controllers/apartment.js";
import verifyToken from "../middlewares/verifyTokenMiddleware.js";




const router = Router();




router.post('/create', createApartment);

router.get('/getAll',verifyToken, getAll);

router.get('/paid',verifyToken, getPaidApartments);

router.put('/update/:id', updateApartment);

router.put('/paidStatus/:apartmentId',verifyToken, markApartmentAsPaid);

router.get('/filter/:month',verifyToken, filterApartmentsByMonth);

router.delete('/Drop/:apartmentId',verifyToken,deleteApartment)







export default router;

